mapper.select:

SELECT * FROM RMVRLIST_1
WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
	AND RONUM <= #{num} * #{lcnt}

		select * from
	(
    select rownum as ronum, a.*
    from (
    select * from rmvrlist
    order by state
    ) a
	)
	
	WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
			AND RONUM <= #{num} * #{lcnt}

		select * from
	(
    select rownum as ronum, a.*
    from (
    select * from rmvrlist
    order by state desc
    ) a
	)
	
	WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
			AND RONUM <= #{num} * #{lcnt}


	SELECT * FROM question
	WHERE ROWNUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
		AND ROWNUM <= #{num} * #{lcnt}
