mapper.select:

SELECT * FROM RMVRLIST_1
WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
	AND RONUM <= #{num} * #{lcnt}

	select * from(select rownum as ronum, a.*from a(select * from rmvrlist order by state ))

WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
		AND RONUM <= #{num} * #{lcnt}

		select * from
	(
    select rownum as ronum, a.*
    from (
    select * from rmvrlist
    order by state desc
    ) a
	)
	
	WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
			AND RONUM <= #{num} * #{lcnt}


	SELECT * FROM v_question
	WHERE RONUM >= (#{num} * #{lcnt}) - (#{lcnt} - 1)  
		AND RONUM <= #{num} * #{lcnt}


SELECT * FROM BUSINESS
WHERE ROWNUM >= (#{pageNum} * #{lcnt}) - (#{lcnt} - 1)  
	AND ROWNUM <= #{pageNum} * #{lcnt}
